import React, { Component } from 'react';
import { NavLink} from 'react-router-dom';
import Moment from 'moment';
class Reservation extends Component {
    state = {
        token: "Bearer eyJhbGciOiJIUzM4NCJ9..KsBMPpCwZLo65uB06l8-DibCCe430yuBh9WTbG1AvuE9k3x7M6jY2XmcMDRNa56F", 
        reservations: []
     }

    componentDidMount(){
    this.fetchReservations();
    }

  fetchReservations = () =>{
    fetch('http://localhost:8080/api/library/admins/reservations', {
       method: 'GET',
       headers: {"Content-Type": "application/json", "Authorization": this.state.token}
      })
    .then(res => res.json())
    .then(res => {
        console.log(res)
      this.setState({reservations: res})

    });
  }

    render() { 
        const {reservations} = this.state;
        if(!reservations.length){
            return (<div>Loading...</div>)
        }
        return ( 
            <section>

            {this.state.reservations.map((reservation, index) =>{
                return(
                <div  key={index} className="card mb-3">
                    <div className="card-body">
                        <h5 className="card-title">#{reservation.id}. {reservation.title}</h5>
                        <h6 className="card-subtitle mb-2 text-muted">{Moment(reservation.dateReservation).format('YYYY-MM-DD')}</h6>
                        <p className="card-text">{reservation.userId}</p>
                        <button type="button" className="btn btn-success">Confirm</button>
                        <button type="button" className="btn btn-warning mx-3">Cancel</button>
                    </div>
                </div>
                )
            })}

            </section>
         );
    }
}

export default Reservation;
