import React, { Component } from 'react';
import Moment from 'moment';
class Reservation extends Component {
    state = {
        prefix: "http://localhost:8080",
        prefix2: "https://puz-biblioteka.herokuapp.com",
        token: "Bearer eyJhbGciOiJIUzM4NCJ9..KsBMPpCwZLo65uB06l8-DibCCe430yuBh9WTbG1AvuE9k3x7M6jY2XmcMDRNa56F", 
        reservations: [],
        username: "",
        notFound: false
     }

    componentDidMount(){
        this.fetchReservations(0, 10, this.state.username);
    }

  fetchReservations = (page, limit, username) =>{
    fetch(this.state.prefix + `/api/library/admins/reservations?page=${page}&limit=${limit}&username=${username}`, {
       method: 'GET',
       headers: {"Content-Type": "application/json", "Authorization": this.state.token}
      })
    .then(res => res.json())
    .then(res => {
        console.log(res)
        if(res.status == '404'){
            console.log("not working");
        }
        this.setState({reservations: res, notFound: false})

    })
    .catch(error =>{
        console.log(error);
        //this.setState({notFound: true});
    })

  }


  confirmReservation = (id) => {
    fetch(`${this.state.prefix}/api/library/admins/confirmBorrow/${id}`,  {
       method: 'POST',
       headers: {
        "Content-Type": "application/json",
        "Authorization": this.state.token
    }
      })
      .then(res => {
          console.log(res);
          if(res.status === 200){
              alert("Reservation confirmed");
          }
          //delete this element from list
      })
  }

  cancelReservation = (id) => {
    fetch(`${this.state.prefix}/api/library/admins/reservations/cancel/${id}`,  {
       method: 'POST',
       headers: {
        "Content-Type": "application/json",
        "Authorization": this.state.token
    }
      })
      .then(res => {
          console.log(res);
          if(res.status === 200){
              alert("Reservation canceled");
          }
          //delete this element from list
      })
  }

  usernameHandler = (name) =>{
    this.setState({username: name.target.value});
}   

  filterByUsername = (e) =>{
    e.preventDefault();
    console.log(this.state.username);
    this.fetchReservations(0, 10, this.state.username);
  }

    render() { 
        const {reservations, notFound, username} = this.state;
        if(!reservations.length && notFound===false){
            return (<div>Loading...</div>)
        }
        else if(!reservations.length && notFound === true){
            return (<div>Username '{username}' not found</div>)
        }
        return ( 
            <section>
            <form className="d-flex justify-content-center mb-5" onClick={this.filterByUsername}>
                <div className="col-auto">
                    <p className="center-text mx-5">Find by username</p>
                </div>
                <div className="col-auto">
                    <label for="inputusername" className="visually-hidden">Username</label>
                    <input type="text" className="form-control" id="inputusername" placeholder="username" value={username} onChange={this.usernameHandler}/>
                </div>
                <div className="col-auto">
                    <button type="submit" className="btn btn-primary mb-3 mx-3" >Filter</button>
                </div>
            </form>
            {this.state.reservations.map((reservation, index) =>{
                return(
                <div  key={index} className="card mb-3">
                    <div className="card-body">
                        <h5 className="card-title">#{reservation.id}. {reservation.title}</h5>
                        <h6 className="card-subtitle mb-2 text-muted">{Moment(reservation.dateReservation).format('YYYY-MM-DD')}</h6>
                        <p className="card-text">{reservation.username}</p>
                        <button type="button" className="btn btn-success" onClick={() => this.confirmReservation(reservation.id)}>Confirm</button>
                        <button type="button" className="btn btn-warning mx-3" onClick={() => this.cancelReservation(reservation.id)}>Cancel</button>
                    </div>
                </div>
                )
            })}

            </section>
         );
    }
}

export default Reservation;
