import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import Moment from 'moment';

class Details extends Component {
    state = { 
        id: 0,
        notFound: false,
        errorMessage: "",
        reservation: null,
        user: null
     }

     componentDidMount(){
        const url = this.props.location.pathname;
        const array = url.split('/');
        const _id = array[array.length-1];

        this.fetchReservation(_id);

        this.setState({id: _id});

     }


  fetchReservation = (id) =>{
    fetch(this.props.prefix + `/api/library/admins/reservations/${id}`, {
       method: 'GET',
       headers: {"Content-Type": "application/json", "Authorization": this.props.token}
      })
    .then(res => res.json())
    .then(res => {
        console.log(res)
        if(res.status === 404){
            this.setState({notFound: true, errorMessage: res.message});
        }
        else{
            this.fetchUser(res.firstName);
            this.setState({reservation: res, notFound: false})
        }       
    })
  }

  fetchUser= (id) =>{
    fetch(this.props.prefix + `/api/library/admins/users/${id}`, {
       method: 'GET',
       headers: {"Content-Type": "application/json", "Authorization": this.props.token}
      })
    .then(res => res.json())
    .then(res => {
        console.log(res)
        if(res.status === 404){
            this.setState({notFound: true, errorMessage: res.message});
        }
        else{
            this.setState({user: res, notFound: false})
        }       
    })
  }

    render() { 
        const {reservation, user, notFound, errorMessage} = this.state;
        if(reservation === null && notFound===false){
            return (<div>Loading...</div>)
        }
        else if(notFound === true){
            return (
                <section>
                    <div><b>{errorMessage}</b></div> 
                </section>
            )
        }
        return ( 
            <section>         
                <div className="card-body">
                        <h5 className="card-title">#{reservation.id}. {reservation.title}</h5>
                        <h6 className="card-subtitle mb-2 text-muted">User: {reservation.username}, </h6>
                        <p className="card-text">User: {user.firstName} {user.lastName}</p>
                        <p className="card-text"></p>
                        <p className="card-text"></p>
                        <p className="card-text">Issue date: {Moment(reservation.dateReservation).format('YYYY-MM-DD')}</p>
                        <button type="button" className="btn btn-success" onClick={() => this.confirmReservation(reservation.id)}>Confirm</button>
                        <button type="button" className="btn btn-warning ms-2" onClick={() => this.cancelReservation(reservation.id)}>Cancel</button>
                    </div>
            </section>
         );
    }
}

export default withRouter(Details);
