import React, { Component } from 'react';

class Login extends Component {
    state = { 
        username: "",
        password: ""
     }

    usernameHandler = (name) =>{
        this.setState({username: name});
    }

    passwordHandler = (pass) =>{
        this.setState({password: pass});
    }

    submitHandler = (e) => {
        e.preventDefault();
        let loginObject = {username, password};
        var errorFlag = false;

        fetch('login', {
            method: 'POST',
            body: JSON.stringify(loginObject),
            headers: { 'Content-Type': 'application/json' },
          })
          .catch(error => {
              errorFlag = true;
              console.log(error);
          })

          if(!errorFlag){
              // show error

          }
      }


    render() { 
        const { username, password} = this.state;
        return ( 
            <section>
            <form className="dropdown-menu p-4" onSubmit={this.submitHandler}>
                <div className="mb-3">
                    <label for="usernameInput1" className="form-label">Username</label>
                    <input type="email" className="form-control" id="usernameInput1" placeholder="username"  value={username} onChange={this.usernameHandler}/>
                </div>
                <div class="mb-3">
                    <label for="passwordInput1" className="form-label">Password</label>
                    <input type="password" className="form-control" id="passwordInput1" placeholder="Password" value={password} onChange={this.passwordHandler}/>
                </div>
                <button type="submit" className="btn btn-primary">Sign in</button>
            </form>
          </section>
         );
    }
}

export default Login;
