    state = {
        id: "",
        email: "",
        firstName: "",
        lastName: "",
        address: "",
        phone: "",
        notFound: false,
        errorMessage: "",
        user: {}
     }

    componentDidMount(){
        const url = this.props.location.pathname;
        const array = url.split('/');
        const _id = array[array.length-1];

        this.fetchUser(_id);
        this.setState({id: _id});

    }


    fetchUser= (id) =>{
        fetch(this.props.prefix + `/api/library/admins/users/${id}`, {
           method: 'GET',
